    DialogueDon(FenetreCECDoc proprietaire, TokensTheme theme, List<Image> iconesApplication) {
        super(proprietaire, "Me faire un don", true);
        this.fenetrePrincipale = Objects.requireNonNull(proprietaire, "proprietaire");
        this.theme = Objects.requireNonNull(theme, "theme");
        if (iconesApplication != null && !iconesApplication.isEmpty()) {
            setIconImages(iconesApplication);
        }

        this.labelRetourCopie = new JLabel(" ");
        this.panneauRetour = new JPanel();

        construireInterface();
        setResizable(false);
        pack();
        appliquerLargeurInitiale();
        setLocationRelativeTo(proprietaire);
    }

    private static BufferedImage lireLogoPngDepuisBase64(String logoBase64) {
        if (logoBase64 == null || logoBase64.isBlank()) {
            return null;
        }
        try {
            byte[] donnees = Base64.getMimeDecoder().decode(logoBase64);
            try (ByteArrayInputStream entree = new ByteArrayInputStream(donnees)) {
                return ImageIO.read(entree);
            }
        } catch (IllegalArgumentException | IOException ex) {
            return null;
        }
    }

    private void construireInterface() {
        PanneauFondDon panneauRacine = new PanneauFondDon(theme);
        panneauRacine.setLayout(new BorderLayout(0, theme.spacing().blockGap()));
        panneauRacine.setBorder(new EmptyBorder(theme.spacing().cardInset(), theme.spacing().cardInset(), theme.spacing().cardInset(), theme.spacing().cardInset()));

        JPanel panneauEntete = creherPanneauEntete();
        JPanel panneauLogos = creherPanneauLogos();
        configurerPanneauRetour();

        panneauRacine.add(panneauEntete, BorderLayout.NORTH);
        panneauRacine.add(panneauLogos, BorderLayout.CENTER);
        panneauRacine.add(panneauRetour, BorderLayout.SOUTH);

        setContentPane(panneauRacine);
        getRootPane().registerKeyboardAction(e -> dispose(), KeyStroke.getKeyStroke("ESCAPE"), JComponent.WHEN_IN_FOCUSED_WINDOW);

        getAccessibleContext().setAccessibleName("Fentre de don");
        getAccessibleContext().setAccessibleDescription("Permet de copier une adresse Bitcoin ou Monero et d'ouvrir GitHub Sponsors");
        labelRetourCopie.getAccessibleContext().setAccessibleName("Confirmation de copie");
        labelRetourCopie.getAccessibleContext().setAccessibleDescription("Affiche temporairement le statut de copie dans le presse-papiers");
    }

