        panneauEntete.setBorder(new EmptyBorder(margeVerticale, margeHorizontale, margeVerticale, margeHorizontale));

        JLabel labelTitre = new JLabel("Me faire un don", SwingConstants.CENTER);
        labelTitre.setAlignmentX(Component.CENTER_ALIGNMENT);
        labelTitre.setFont(theme.typography().title().deriveFont(Font.BOLD, theme.typography().title().getSize2D() + 2f));
        labelTitre.setForeground(theme.palette().inverseText());

        JLabel labelSousTitre = new JLabel("<html><div style='text-align:center'>Si tu en as les moyens et l'envie, un *petit* tip est toujours bienvenu.</div></html>", SwingConstants.CENTER);
        labelSousTitre.setAlignmentX(Component.CENTER_ALIGNMENT);
        labelSousTitre.setFont(theme.typography().helper());
