        panneauLogos.setLayout(new BoxLayout(panneauLogos, BoxLayout.X_AXIS));

        BufferedImage logoBitcoin = lireLogoPngDepuisBase64(LOGO_BITCOIN_PNG_BASE64);
        BufferedImage logoMonero = lireLogoPngDepuisBase64(LOGO_MONERO_PNG_BASE64);
        BufferedImage logoGithub = lireLogoPngDepuisBase64(LOGO_GITHUB_PNG_BASE64);

        BoutonLogoDon boutonBitcoin = new BoutonLogoDon("Bitcoin", logoBitcoin, theme);
        boutonBitcoin.setMnemonic('B');
        boutonBitcoin.addActionListener(e -> selectionnerAdresse(TypeDon.BITCOIN));
        boutonBitcoin.setToolTipText("Copier l'adresse Bitcoin");
        boutonBitcoin.getAccessibleContext().setAccessibleName("Adresse Bitcoin");
