    private void selectionnerAdresse(TypeDon typeDon) {
        if (typeDon == null) {
            return;
        }
        boolean copieReussie = copierDansPressePapiers(typeDon.adresse());
        if (copieReussie) {
            afficherMessageTemporaire("Adresse " + typeDon.libelle() + " copie dans le presse-papiers.", theme.palette().success());
        } else {
            afficherMessageTemporaire("Copie impossible pour le moment.", theme.palette().error());
        }
    }

    private void ouvrirPageGithubSponsors() {
        fenetrePrincipale.ouvrirLienExterneDansNavigateur(URI_GITHUB_SPONSORS_RD_RUNTIME, "Me faire un don", "Ouverture du navigateur indisponible sur ce systme.", "Impossible d'ouvrir la page GitHub Sponsors dans le navigateur.", "Le lien GitHub Sponsors a t copi dans le presse-papiers.");
    }

    private boolean copierDansPressePapiers(String texte) {
        try {
            Toolkit.getDefaultToolkit().getSystemClipboard().setContents(new StringSelection(texte), null);
            return true;
        } catch (IllegalStateException | HeadlessException | SecurityException ex) {
            return false;
        }
    }

    private void appliquerLargeurInitiale() {
        int largeurActuelle = getWidth();
        int largeurCible = Math.max(largeurActuelle, LARGEUR_MINIMALE);
        GraphicsConfiguration configuration = getGraphicsConfiguration();
        Rectangle zoneEcran = configuration == null ? new Rectangle(Toolkit.getDefaultToolkit().getScreenSize()) : configuration.getBounds();
        Insets margesEcran = configuration == null ? new Insets(0, 0, 0, 0) : Toolkit.getDefaultToolkit().getScreenInsets(configuration);
        int largeurMax = Math.max(320, zoneEcran.width - margesEcran.left - margesEcran.right - 64);
        largeurFixe = Math.min(largeurCible, largeurMax);
        setSize(largeurFixe, getHeight());
    }

    private void afficherMessageTemporaire(String message, Color couleurBase) {
        if (minuterieFondu != null && minuterieFondu.isRunning()) {
            minuterieFondu.stop();
        }
        String messageFinal = message == null ? "" : message;
        Color couleur = couleurBase == null ? theme.palette().bodyText() : couleurBase;
        labelRetourCopie.setText(messageFinal);
        labelRetourCopie.setForeground(new Color(couleur.getRed(), couleur.getGreen(), couleur.getBlue(), 255));

        int cadence = 40;
        int etapes = Math.max(1, DUREE_FONDU_MS / cadence);
        int[] iteration = {0};
        minuterieFondu = new Timer(cadence, e -> {
            iteration[0]++;
            float progression = Math.min(1f, iteration[0] / (float) etapes);
            int alpha = Math.max(0, Math.round(255f * (1f - progression)));
            labelRetourCopie.setForeground(new Color(couleur.getRed(), couleur.getGreen(), couleur.getBlue(), alpha));
            if (progression >= 1f) {
                minuterieFondu.stop();
                minuterieFondu = null;
                labelRetourCopie.setText(" ");
                labelRetourCopie.setForeground(new Color(couleur.getRed(), couleur.getGreen(), couleur.getBlue(), 0));
            }
        });
        minuterieFondu.start();
    }

